// 전역 데이터 상태 (차후 서버 연동 대비)
import { createContext, useContext, useState, useEffect } from "react";
import { collections, userData } from "../services/storage/localStorage";
import { errorMsg } from "../components/common/errorMessage";

// context 생성
export const DataContext = createContext();

// provider 컴포넌트
export const DataProvider = ({ children }) => {
  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  // ● 상태 초기화
  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  const [collectionsData, setCollectionsData] = useState(null);
  const [currentUserId, setCurrentUserId] = useState(null); //-- 차후 유저 정보 추가 시 수정
  const [currentPermission, setCurrentPermission] = useState(null); //-- 차후 유저 정보 추가 시 수정
  const [uiState, setUiState] = useState({
    currentProjectId: null, //-- 선택된 프로젝트
    currentEpisodeId: null, //-- 선택된 에피소드
    currentPageId: null, // 선택된 페이지
    currentCutId: null, // 선택된 커
    // ... 기타 UI 상태 추가하기
  });

  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  // ● 컴포넌트 마운트 시 localStorage에서 데이터 로드
  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  useEffect(() => {
    // localStorage에서 유저 정보 로드, 없을 시 초기 데이터 불러옴
    //-- 차후 초기 데이터 수정 필요
    const savedUser = localStorage.getItem("user");
    const user = savedUser ? JSON.parse(savedUser) : userData;
    setCurrentUserId(user.userId);

    // localStorage에서 전체 컬렉션 데이터 읽기, 없을 시 초기 데이터 불러옴
    //-- 차후 초기 데이터 수정 필요
    const savedData = localStorage.getItem("collectionsData");
    const data = savedData ? JSON.parse(savedData) : collections;
    setCollectionsData(data); //--컬렉션 전체

    // 현재 유저의 컬렉션에서 첫 번째 프로젝트/에피소드를 current로 설정
    const userCollection = data[user.userId];
    //----- 이하 차후 프로젝트 정보 추가 시 수정 -----//
    if (userCollection) {
      const firstProjectId = userCollection.projectOrder[0];
      const firstProject = userCollection.projects[firstProjectId];
      const firstEpisodeId = firstProject?.episodeOrder[0];

      setUiState({
        currentProjectId: firstProjectId,
        currentEpisodeId: firstEpisodeId,
        currentPageId: null,
        currentCutId: null,
      });
    }

    // 권한 설정
    //-- 차후 유저 정보 추가 시 수정
    setCurrentPermission(userCollection.permissions[user.userId]);
  }, []);
  //-- dependencies []인 상황, 차후 수정 필요 가능성 있음

  // 차후 서버 요청 시 아래 함수 사용
  const loadCollectionsData = async (userId) => {
    const response = await fetch(`/api/collections/my-collections`);
    const data = await response.json();

    setCollectionsData(data);

    // 현재 유저의 권한 계산
    const permission = data.collection.permissions[userId];
    setCurrentPermission(permission); // 'owner' | 'editor' | 'commentonly' | 'readonly'
  };

  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  // ● 데이터 변경 시 localStorage에 저장
  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  useEffect(() => {
    if (collectionsData) {
      localStorage.setItem("collectionsData", JSON.stringify(collectionsData));
    }
  }, [collectionsData]);

  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  // ● 헬퍼 함수: id 기반으로 접근
  //   - 공유 기능 대비 모든 helpers 함수에 userId를 매개면수로 넣음
  //     (기본값은 currentUserId)
  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  const helpers = {
    //--● 컬렉션 (현재 유저 기준)
    getUserCollection: (userId = currentUserId) => {
      //-- 차후 수정 필요
      if (!userId) return null;
      return collectionsData?.[userId];
    },

    //--● 프로젝트 관련
    getProjects: (userId = currentUserId) => {
      const userCollection = helpers.getUserCollection(userId);
      return userCollection?.projects;
    },
    getProject: (projectId = "initial-project", userId = currentUserId) => {
      //-- 차후 초기값 수정 필요
      if (!projectId) return null;
      const userCollection = helpers.getUserCollection(userId);
      return userCollection?.projects?.[projectId];
    },
    getCurrentProject: () => {
      //-- 차후수정필요
      if (!uiState.currentProjectId) return null;
      return helpers.getProject(uiState.currentProjectId);
    },

    //--● 에피소드 관련
    getEpisodes: (projectId = "initial-project", userId = currentUserId) => {
      const project = helpers.getProject(projectId, userId);
      return project?.episodes;
    },
    getEpisode: (
      projectId = "initial-project",
      episodeId = "episode-0001",
      userId = currentUserId
    ) => {
      //-- 차후 초기값 수정 필요
      if (!projectId || !episodeId) return null;
      const project = helpers.getProject(projectId, userId);
      return project?.episodes?.[episodeId];
    },
    getCurrentEpisode: () => {
      if (!uiState.currentProjectId || !uiState.currentEpisodeId) return null;
      return helpers.getEpisode(
        uiState.currentProjectId,
        uiState.currentEpisodeId
      );
    },

    //--● 페이지 관련
    getPages: (
      projectId = "initial-project",
      episodeId = "episode-0001",
      userId = currentUserId
    ) => {
      const episode = helpers.getEpisode(projectId, episodeId, userId);
      return episode?.pages;
    },
    getPage: (
      projectId = "initial-project",
      episodeId = "episode-0001",
      pageId,
      userId = currentUserId
    ) => {
      if (!projectId || !episodeId || pageId) return null;
      const episode = helpers.getEpisode(projectId, episodeId, userId);
      return episode?.pages?.[pageId];
    },
    getCurrentPage: () => {
      if (
        !uiState.currentProjectId ||
        !uiState.currentEpisodeId ||
        !uiState.currentPageId
      )
        return null;
      return helpers.getPage(
        uiState.currentProjectId,
        uiState.currentEpisodeId,
        uiState.currentPageId
      );
    },

    //--● 컷 관련
    getCuts: (
      projectId = "initial-project",
      episodeId = "episode-0001",
      pageId,
      userId = currentUserId
    ) => {
      const page = helpers.getPage(projectId, episodeId, pageId, userId);
      return page?.cuts;
    },
    getCut: (
      projectId = "initial-project",
      episodeId = "episode-0001",
      pageId,
      cutId,
      userId = currentUserId
    ) => {
      if (!projectId || !episodeId || !pageId || !cutId) return null;
      const page = helpers.getPage(projectId, episodeId, pageId, userId);
      return page?.cuts?.[cutId];
    },
    getCurrentCut: () => {
      const {
        currentProjectId,
        currentEpisodeId,
        currentPageId,
        currentCutId,
      } = uiState;
      if (
        !currentProjectId ||
        !currentEpisodeId ||
        !currentPageId ||
        !currentCutId
      )
        return null;
      return helpers.getCut(
        currentProjectId,
        currentEpisodeId,
        currentPageId,
        currentCutId
      );
    },
  };

  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  // ● 반환 변수 value 선언
  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  const value = {
    // 원본 상태 값
    collectionsData,
    setCollectionsData,
    currentUserId,
    setCurrentUserId,
    currentPermission,
    uiState,
    setUiState,

    // 헬퍼 함수
    ...helpers,
  };
  return <DataContext.Provider value={value}>{children}</DataContext.Provider>;
};

// useData 커스텀 훅
export default function useData() {
  const context = useContext(DataContext);

  // Context 밖에서 사용하면 에러
  if (!context) {
    throw new Error(errorMsg.contexts["useData 오류"]);
  }

  return context;
}
