// 전역 데이터 상태 (차후 서버 연동 대비)
import { createContext, useContext, useState } from "react";
import { collections } from "../services/storage/localStorage";

// context 생성
export const DataContext = createContext();

// provider 컴포넌트
export const DataProvider = ({ children, collections = collections }) => {
  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  // ● projectState: 서버와 동기화될 데이터
  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  const [projectState, setProjectState] = useState(collections);

  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  // ● uiState: 로컬 UI 상태 (서버 저장 X)
  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  const [uiState, setUiState] = useState({
    currentEpisodeId: collections.project.episodeOrder[0], // 선택된 에피소드
    currentPageId: null, // 선택된 페이지
    // ... 기타 UI 상태
  });

  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  // ● 헬퍼 함수: id 기반으로 접근
  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  const helpers = {
    // 프로젝트 전체
    getProjects: () => projectState.project,
    getProject: (projectId) => projectState.project[projectId],

    // 에피소드 관련
    getEpisodes: () => projectState.project.episodes,
    getEpisode: (episodeId) => projectState.project.episodes[episodeId],

    getCurrentEpisode: () =>
      projectState.project.episodes[uiState.currentEpisodeId],

    // 페이지 관련
    getPage: (episodeId, pageId) =>
      projectState.project.episodes[episodeId]?.pages[pageId],

    getCurrentPage: () => {
      const episode = helpers.getCurrentEpisode();
      return episode?.pages[uiState.currentPageId];
    },

    // 컷 관련
    getCut: (episodeId, pageId, cutId) =>
      projectState.project.episodes[episodeId]?.pages[pageId]?.cuts[cutId],
  };

  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  // ● 반환 변수 value 선언
  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  const value = {
    // 상태 값
    projectState,
    setProjectState,
    uiState,
    setUiState,

    // 헬퍼 함수
    //-- 현재 에피소드 객체???
    ...helpers,
  };
  return <DataContext.Provider value={value}>{children}</DataContext.Provider>;
};

// useData 커스텀 훅
export default function useData() {
  const context = useContext(DataContext);

  // Context 밖에서 사용하면 에러
  if (!context) {
    throw new Error("useData는 DataProvider 안에서만 사용 가능합니다");
  }

  return context;
}
