import "assets/css/variables.css"; // 전역 변수 로드
import "assets/css/global.css"; // 전역 스타일 로드
import "./styles/common/layout.css"; // 공통 레이아웃
import "./styles/common/button.css"; // 공통 컴포넌트
import { useState } from "react";
import { AuthProvider } from "./contexts/AuthContext";
import { DataProvider } from "./contexts/DataContext";
import { Navigate, Route, Routes } from "react-router-dom";
import CollectionManagement from "./components/screens/Collection";
import ProjectManagement from "./components/screens/Project";
import EpisodeManagement from "./components/screens/Episode";
import PageManagement from "./components/screens/Page";
import CutManagement from "./components/screens/Cut";
import MainLayout from "./components/layout/MainLayout";

function App() {
  const [currentScreen, setCurrentScreen] = useState("page");

  return (
    <AuthProvider>
      <DataProvider>
        <Routes>
          <Route path="/" element={<MainLayout />}>
            <Route index element={<Navigate to="/pages" />} />
            <Route path="pages" element={<CollectionManagement />} />
            <Route path="pages" element={<ProjectManagement />} />
            <Route path="pages" element={<EpisodeManagement />} />
            <Route path="pages" element={<PageManagement />} />
            <Route path="pages/:pageId/cuts" element={<CutManagement />} />
          </Route>
        </Routes>
      </DataProvider>
    </AuthProvider>
  );
}
export default App;
