// 전역 데이터 상태 (차후 서버 연동 대비)
import { createContext, useState } from "react";
import { initialData } from "../services/storage/localStorage";

// context 생성
export const DataContext = createContext();

// provider 컴포넌트
export const DataProvider = ({ children, initialData = { initialData } }) => {
  // 전체 데이터 state
  const [data, setData] = useState(initialData);

  // context 훅
  const value = {
    // 원본 데이터(읽기 전용)
    data,
    // 원본 데이터 수정 함수
    setData,

    // 헬퍼 값들(자주 쓰는 데이터)
    //-- 현재 에피소드 객체???
    currentEpisode: data.project.episodes[data.project.episodeOrder[0]],
    //-- 현재 에피소드 아이디
    currentEpisodeId: data.project.episodeOrder[0],

    // 편의 함수
    //--
    getProject: () => {},
    //--
    getEpisode: (episodeId) => data.project.episodes[episodeId],
    //--
    getPage: (episode, pageId) =>
      data.project.episodes[episodeId].pages[pageId],
  };
  return <DataContext.Provider value={value}>{children}</DataContext.Provider>;
};

