// 전역 데이터 상태 (차후 서버 연동 대비)
import { createContext, useContext, useState } from "react";
import { collections } from "../services/storage/localStorage";
import { errorMsg } from "../components/common/errorMessage";

// context 생성
export const DataContext = createContext();

// provider 컴포넌트
export const DataProvider = ({ children }) => {
  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  // ● 상태 초기화
  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  // const { user } = useUser(); //-- 차후 유저 정보 추가 시 사용
  const [collectionData, setCollectionData] = useState(null);
  const [uiState, setUiState] = useState({
    currentEpisodeId: collections.project.episodeOrder[0], //-- 선택된 에피소드, 차후 초기값 수정
    currentPageId: null, // 선택된 페이지
    // ... 기타 UI 상태 추가하기
  });
  const [currentPermission, setCurrentPermission] = useState("owner"); //-- 차후 유저 정보 추가 시 수정

  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  // ● 컴포넌트 마운트 시 localStorage에서 데이터 로드
  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  useEffect(() => {
    if (user) {
      loadCollectionData(user.userId);
    }
  }, [user]);

  const loadCollectionData = async (userId) => {
    // 차후 서버 요청 시
    // const response = await fetch(`/api/collections/my-collections`);
    // const data = await response.json();

    setCollectionData(data);

    // ✅ 현재 유저의 권한 계산
    const permission = data.collection.permissions[userId];
    setCurrentPermission(permission); // 'owner' | 'editor' | 'commentonly' | 'readonly'
  };
  /*
  <DataContext.Provider
    value={{
      collectionData,
      currentPermission, // ✅ 권한 정보
      setCollectionData,
      // ... 헬퍼 함수들
    }}
  >
    {children}
  </DataContext.Provider>;
  */

  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  // ● uiState: 로컬 UI 상태 (서버 저장 X)
  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━

  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  // ● 헬퍼 함수: id 기반으로 접근
  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  const helpers = {
    // 프로젝트 전체
    getProjects: () => projectState.project,
    getProject: (projectId) => projectState.project[projectId],

    // 에피소드 관련
    getEpisodes: () => projectState.project.episodes,
    getEpisode: (episodeId) => projectState.project.episodes[episodeId],

    getCurrentEpisode: () =>
      projectState.project.episodes[uiState.currentEpisodeId],

    // 페이지 관련
    getPage: (episodeId, pageId) =>
      projectState.project.episodes[episodeId]?.pages[pageId],

    getCurrentPage: () => {
      const episode = helpers.getCurrentEpisode();
      return episode?.pages[uiState.currentPageId];
    },

    // 컷 관련
    getCut: (episodeId, pageId, cutId) =>
      projectState.project.episodes[episodeId]?.pages[pageId]?.cuts[cutId],
  };

  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  // ● 반환 변수 value 선언
  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  const value = {
    // 상태 값
    projectState,
    setProjectState,
    uiState,
    setUiState,

    // 헬퍼 함수
    //-- 현재 에피소드 객체???
    ...helpers,
  };
  return <DataContext.Provider value={value}>{children}</DataContext.Provider>;
};

// useData 커스텀 훅
export default function useData() {
  const context = useContext(DataContext);

  // Context 밖에서 사용하면 에러
  if (!context) {
    throw new Error(errorMsg.contexts["useData 오류"]);
  }

  return context;
}
