import { useState } from "react";
import { AuthProvider } from "./contexts/AuthContext";
import { DataProvider } from "./contexts/DataContext";
import CutManagement from "./components/screens/Cut";
import PageManagement from "./components/screens/Page";
import EpisodeManagement from "./components/screens/Episode";
import ProjectManagement from "./components/screens/Project";
import CollectionManagement from "./components/screens/Collection";

function App() {
  const [currentScreen, setCurrentScreen] = useState("page");

  return (
    <div>
      <AuthProvider>
        <DataProvider>
          <button>컬렉션</button>
          <button>프로젝트</button>
          <button>에피소드</button>
          <button>페이지</button>
          <button>컷</button>
          {/* 전역 데이터만 관리 */}
          {/* 차후 project, episode 추가 */}
          {currentScreen === "collection" && <CollectionManagement />}
          {currentScreen === "project" && <ProjectManagement />}
          {currentScreen === "episode" && <EpisodeManagement />}
          {currentScreen === "page" && <PageManagement />}
          {currentScreen === "cut" && <CutManagement />}
        </DataProvider>
      </AuthProvider>
    </div>
  );
}

export default App;

