import React, { useState } from "react";
import {
  Grid,
  Layout,
  BookOpen,
  Eye,
  Edit3,
  MessageSquare,
  Plus,
  Settings,
  Users,
  Download,
  Share2,
} from "lucide-react";

const StoryboardUIShowcase = () => {
  const [selectedDesign, setSelectedDesign] = useState("dark");
  const [selectedPage, setSelectedPage] = useState(5);
  const [hoveredPage, setHoveredPage] = useState(null);

  // 샘플 페이지 데이터
  const samplePages = Array.from({ length: 24 }, (_, i) => ({
    pageId: `page-${String(i + 1).padStart(4, "0")}`,
    pageNumber: i + 1,
    memo:
      i % 3 === 0
        ? "주요 장면: 캐릭터 등장"
        : i % 5 === 0
        ? "배경 상세 작업 필요"
        : "",
    cutCount: Math.floor(Math.random() * 5) + 2,
    hasComments: i % 4 === 0,
  }));

  const designs = {
    dark: {
      name: "Dark Professional",
      desc: "장시간 작업에 최적화된 다크 테마",
      colors: {
        bg: "#1a1a1a",
        surface: "#252525",
        surfaceHover: "#2d2d2d",
        border: "#3a3a3a",
        primary: "#5B9FFF",
        primaryHover: "#4a8eee",
        text: "#e0e0e0",
        textSecondary: "#999",
        accent: "#FF6B9D",
        success: "#4ADE80",
      },
    },
    light: {
      name: "Light Creative",
      desc: "밝고 친근한 크리에이티브 스타일",
      colors: {
        bg: "#f8f9fa",
        surface: "#ffffff",
        surfaceHover: "#f1f3f5",
        border: "#e9ecef",
        primary: "#4263EB",
        primaryHover: "#364FC7",
        text: "#212529",
        textSecondary: "#6c757d",
        accent: "#FF6B9D",
        success: "#51CF66",
      },
    },
    minimal: {
      name: "Minimal Focus",
      desc: "작업 집중을 위한 미니멀 디자인",
      colors: {
        bg: "#fafafa",
        surface: "#ffffff",
        surfaceHover: "#f5f5f5",
        border: "#e0e0e0",
        primary: "#000000",
        primaryHover: "#333333",
        text: "#1a1a1a",
        textSecondary: "#757575",
        accent: "#6366F1",
        success: "#10B981",
      },
    },
    webtoon: {
      name: "Webtoon Native",
      desc: "웹툰/만화 제작 특화 인터페이스",
      colors: {
        bg: "#0f172a",
        surface: "#1e293b",
        surfaceHover: "#334155",
        border: "#475569",
        primary: "#F59E0B",
        primaryHover: "#D97706",
        text: "#f1f5f9",
        textSecondary: "#94a3b8",
        accent: "#EC4899",
        success: "#34D399",
      },
    },
  };

  const currentDesign = designs[selectedDesign];
  const c = currentDesign.colors;

  const pageLayoutStyles = {
    dark: "shadow-lg",
    light: "shadow-md",
    minimal: "border-2",
    webtoon: "shadow-2xl",
  };

  const renderDesignSelector = () => (
    <div
      style={{
        display: "flex",
        gap: "12px",
        marginBottom: "24px",
        padding: "16px",
        background: c.surface,
        borderRadius: "12px",
        border: `1px solid ${c.border}`,
      }}
    >
      {Object.entries(designs).map(([key, design]) => (
        <button
          key={key}
          onClick={() => setSelectedDesign(key)}
          style={{
            flex: 1,
            padding: "16px",
            background: selectedDesign === key ? c.primary : "transparent",
            color: selectedDesign === key ? "#fff" : c.text,
            border: `2px solid ${
              selectedDesign === key ? c.primary : c.border
            }`,
            borderRadius: "8px",
            cursor: "pointer",
            transition: "all 0.2s",
            fontWeight: selectedDesign === key ? "600" : "400",
          }}
          onMouseEnter={(e) => {
            if (selectedDesign !== key) {
              e.target.style.borderColor = c.primary;
              e.target.style.background = c.surfaceHover;
            }
          }}
          onMouseLeave={(e) => {
            if (selectedDesign !== key) {
              e.target.style.borderColor = c.border;
              e.target.style.background = "transparent";
            }
          }}
        >
          <div
            style={{ fontSize: "14px", fontWeight: "600", marginBottom: "4px" }}
          >
            {design.name}
          </div>
          <div
            style={{
              fontSize: "11px",
              opacity: 0.7,
              color: selectedDesign === key ? "#fff" : c.textSecondary,
            }}
          >
            {design.desc}
          </div>
        </button>
      ))}
    </div>
  );

  const renderTopBar = () => (
    <div
      style={{
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        padding: "16px 24px",
        background: c.surface,
        borderBottom: `1px solid ${c.border}`,
      }}
    >
      <div style={{ display: "flex", alignItems: "center", gap: "16px" }}>
        <BookOpen size={24} color={c.primary} />
        <div>
          <div style={{ color: c.text, fontSize: "18px", fontWeight: "600" }}>
            Episode 01
          </div>
          <div style={{ color: c.textSecondary, fontSize: "12px" }}>
            24 pages · 작업 중
          </div>
        </div>
      </div>

      <div style={{ display: "flex", gap: "8px" }}>
        <IconButton icon={Users} label="공동작업" color={c} />
        <IconButton icon={Share2} label="공유" color={c} />
        <IconButton icon={Download} label="내보내기" color={c} />
        <IconButton icon={Settings} label="설정" color={c} />
      </div>
    </div>
  );

  const renderLeftSidebar = () => (
    <div
      style={{
        width: "280px",
        background: c.surface,
        borderRight: `1px solid ${c.border}`,
        display: "flex",
        flexDirection: "column",
      }}
    >
      <div
        style={{
          padding: "20px",
          borderBottom: `1px solid ${c.border}`,
        }}
      >
        <h3
          style={{
            color: c.text,
            fontSize: "14px",
            fontWeight: "600",
            marginBottom: "12px",
          }}
        >
          페이지 네비게이션
        </h3>
        <div
          style={{
            display: "flex",
            gap: "8px",
            marginBottom: "16px",
          }}
        >
          <ViewButton active icon={Grid} label="그리드" color={c} />
          <ViewButton icon={Layout} label="리스트" color={c} />
          <ViewButton icon={Eye} label="프리뷰" color={c} />
        </div>
      </div>

      <div
        style={{
          flex: 1,
          overflowY: "auto",
          padding: "16px",
        }}
      >
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(3, 1fr)",
            gap: "8px",
          }}
        >
          {samplePages.slice(0, 12).map((page) => (
            <MiniPageCard
              key={page.pageId}
              page={page}
              isSelected={selectedPage === page.pageNumber}
              onClick={() => setSelectedPage(page.pageNumber)}
              color={c}
            />
          ))}
        </div>
      </div>

      <div
        style={{
          padding: "16px",
          borderTop: `1px solid ${c.border}`,
        }}
      >
        <button
          style={{
            width: "100%",
            padding: "12px",
            background: c.primary,
            color: "#fff",
            border: "none",
            borderRadius: "8px",
            cursor: "pointer",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            gap: "8px",
            fontWeight: "600",
            fontSize: "14px",
          }}
        >
          <Plus size={18} />
          페이지 추가
        </button>
      </div>
    </div>
  );

  const renderMainWorkspace = () => (
    <div
      style={{
        flex: 1,
        display: "flex",
        flexDirection: "column",
        overflow: "hidden",
      }}
    >
      {/* 페이지 헤더 */}
      <div
        style={{
          padding: "20px 32px",
          background: c.surface,
          borderBottom: `1px solid ${c.border}`,
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
        }}
      >
        <div style={{ display: "flex", alignItems: "center", gap: "16px" }}>
          <h2 style={{ color: c.text, fontSize: "24px", fontWeight: "600" }}>
            Page {selectedPage}
          </h2>
          <span
            style={{
              padding: "4px 12px",
              background: c.success + "20",
              color: c.success,
              borderRadius: "12px",
              fontSize: "12px",
              fontWeight: "600",
            }}
          >
            {samplePages[selectedPage - 1]?.cutCount || 0} 컷
          </span>
        </div>

        <div style={{ display: "flex", gap: "8px" }}>
          <ActionButton icon={Edit3} label="편집" color={c} />
          <ActionButton icon={MessageSquare} label="메모" color={c} badge={3} />
        </div>
      </div>

      {/* 메인 작업 영역 */}
      <div
        style={{
          flex: 1,
          overflowY: "auto",
          padding: "32px",
        }}
      >
        {/* 페이지 그리드 뷰 */}
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fill, minmax(200px, 1fr))",
            gap: "24px",
            maxWidth: "1400px",
            margin: "0 auto",
          }}
        >
          {samplePages.map((page) => (
            <PageCard
              key={page.pageId}
              page={page}
              isSelected={selectedPage === page.pageNumber}
              isHovered={hoveredPage === page.pageNumber}
              onClick={() => setSelectedPage(page.pageNumber)}
              onHover={() => setHoveredPage(page.pageNumber)}
              onLeave={() => setHoveredPage(null)}
              color={c}
              styleVariant={selectedDesign}
            />
          ))}
        </div>
      </div>
    </div>
  );

  const renderRightPanel = () => (
    <div
      style={{
        width: "320px",
        background: c.surface,
        borderLeft: `1px solid ${c.border}`,
        display: "flex",
        flexDirection: "column",
      }}
    >
      <div
        style={{
          padding: "20px",
          borderBottom: `1px solid ${c.border}`,
        }}
      >
        <h3
          style={{
            color: c.text,
            fontSize: "14px",
            fontWeight: "600",
            marginBottom: "4px",
          }}
        >
          페이지 메모
        </h3>
        <p style={{ color: c.textSecondary, fontSize: "12px" }}>
          Page {selectedPage}
        </p>
      </div>

      <div
        style={{
          flex: 1,
          padding: "20px",
          overflowY: "auto",
        }}
      >
        <textarea
          placeholder="페이지 메모를 입력하세요..."
          style={{
            width: "100%",
            minHeight: "120px",
            padding: "12px",
            background: c.bg,
            border: `1px solid ${c.border}`,
            borderRadius: "8px",
            color: c.text,
            fontSize: "14px",
            fontFamily: "inherit",
            resize: "vertical",
          }}
          defaultValue={samplePages[selectedPage - 1]?.memo}
        />

        <div style={{ marginTop: "24px" }}>
          <h4
            style={{
              color: c.text,
              fontSize: "13px",
              fontWeight: "600",
              marginBottom: "12px",
              display: "flex",
              alignItems: "center",
              gap: "8px",
            }}
          >
            <MessageSquare size={16} />컷 메모 (3)
          </h4>

          <div style={{ display: "flex", flexDirection: "column", gap: "8px" }}>
            {[1, 2, 3].map((cutNum) => (
              <div
                key={cutNum}
                style={{
                  padding: "12px",
                  background: c.bg,
                  borderRadius: "8px",
                  border: `1px solid ${c.border}`,
                }}
              >
                <div
                  style={{
                    color: c.primary,
                    fontSize: "12px",
                    fontWeight: "600",
                    marginBottom: "4px",
                  }}
                >
                  Cut {cutNum}
                </div>
                <div
                  style={{
                    color: c.textSecondary,
                    fontSize: "12px",
                    lineHeight: "1.5",
                  }}
                >
                  컷 메모 내용...
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );

  return (
    <div
      style={{
        width: "100vw",
        height: "100vh",
        background: c.bg,
        fontFamily:
          '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
        overflow: "hidden",
      }}
    >
      {/* 디자인 선택기 (데모용) */}
      <div
        style={{
          position: "absolute",
          top: "16px",
          left: "50%",
          transform: "translateX(-50%)",
          zIndex: 1000,
          width: "90%",
          maxWidth: "1200px",
        }}
      >
        {renderDesignSelector()}
      </div>

      {/* 메인 레이아웃 */}
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          height: "100%",
          paddingTop: "120px",
        }}
      >
        {renderTopBar()}

        <div
          style={{
            display: "flex",
            flex: 1,
            overflow: "hidden",
          }}
        >
          {renderLeftSidebar()}
          {renderMainWorkspace()}
          {renderRightPanel()}
        </div>
      </div>
    </div>
  );
};

// 헬퍼 컴포넌트들
const IconButton = ({ icon: Icon, label, color }) => (
  <button
    title={label}
    style={{
      padding: "8px 12px",
      background: "transparent",
      border: `1px solid ${color.border}`,
      borderRadius: "6px",
      cursor: "pointer",
      display: "flex",
      alignItems: "center",
      gap: "6px",
      color: color.text,
      fontSize: "13px",
      transition: "all 0.2s",
    }}
    onMouseEnter={(e) => {
      e.currentTarget.style.background = color.surfaceHover;
      e.currentTarget.style.borderColor = color.primary;
    }}
    onMouseLeave={(e) => {
      e.currentTarget.style.background = "transparent";
      e.currentTarget.style.borderColor = color.border;
    }}
  >
    <Icon size={16} />
    <span>{label}</span>
  </button>
);

