const MiniPageCard = ({ page, isSelected, onClick, color }) => (
  <div
    onClick={onClick}
    style={{
      aspectRatio: "1/1.4",
      background: color.bg,
      border: `2px solid ${isSelected ? color.primary : color.border}`,
      borderRadius: "6px",
      cursor: "pointer",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      fontSize: "12px",
      fontWeight: "600",
      color: isSelected ? color.primary : color.textSecondary,
      transition: "all 0.2s",
      position: "relative",
    }}
    onMouseEnter={(e) => {
      if (!isSelected) {
        e.currentTarget.style.borderColor = color.primary;
        e.currentTarget.style.transform = "scale(1.05)";
      }
    }}
    onMouseLeave={(e) => {
      if (!isSelected) {
        e.currentTarget.style.borderColor = color.border;
        e.currentTarget.style.transform = "scale(1)";
      }
    }}
  >
    {page.pageNumber}
    {page.hasComments && (
      <div
        style={{
          position: "absolute",
          top: "4px",
          right: "4px",
          width: "6px",
          height: "6px",
          background: color.accent,
          borderRadius: "50%",
        }}
      />
    )}
  </div>
);

const PageCard = ({
  page,
  isSelected,
  isHovered,
  onClick,
  onHover,
  onLeave,
  color,
  styleVariant,
}) => {
  const getCardStyle = () => {
    const base = {
      aspectRatio: "1/1.4",
      background: color.surface,
      borderRadius: "12px",
      cursor: "pointer",
      display: "flex",
      flexDirection: "column",
      overflow: "hidden",
      transition: "all 0.3s cubic-bezier(0.4, 0, 0.2, 1)",
      position: "relative",
    };

    if (styleVariant === "dark") {
      return {
        ...base,
        border: `2px solid ${isSelected ? color.primary : color.border}`,
        boxShadow: isHovered
          ? "0 20px 60px rgba(0,0,0,0.5)"
          : "0 4px 12px rgba(0,0,0,0.3)",
      };
    } else if (styleVariant === "light") {
      return {
        ...base,
        border: `2px solid ${isSelected ? color.primary : color.border}`,
        boxShadow: isHovered
          ? "0 12px 40px rgba(0,0,0,0.15)"
          : "0 2px 8px rgba(0,0,0,0.08)",
      };
    } else if (styleVariant === "minimal") {
      return {
        ...base,
        border: `3px solid ${isSelected ? color.primary : color.border}`,
        boxShadow: "none",
      };
    } else {
      return {
        ...base,
        border: `2px solid ${isSelected ? color.primary : color.border}`,
        boxShadow: isHovered
          ? "0 20px 60px rgba(0,0,0,0.6), 0 0 0 1px rgba(245,158,11,0.3)"
          : "0 8px 24px rgba(0,0,0,0.4)",
      };
    }
  };

  return (
    <div
      onClick={onClick}
      onMouseEnter={onHover}
      onMouseLeave={onLeave}
      style={{
        ...getCardStyle(),
        transform: isHovered ? "translateY(-8px)" : "translateY(0)",
      }}
    >
      {/* 페이지 프리뷰 영역 */}
      <div
        style={{
          flex: 1,
          background: `linear-gradient(135deg, ${color.bg} 0%, ${color.border} 100%)`,
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          position: "relative",
        }}
      >
        <div
          style={{
            width: "80%",
            height: "80%",
            border: `2px dashed ${color.border}`,
            borderRadius: "8px",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            color: color.textSecondary,
            fontSize: "24px",
            fontWeight: "700",
          }}
        >
          {page.pageNumber}
        </div>

        {/* 컷 인디케이터 */}
        <div
          style={{
            position: "absolute",
            bottom: "8px",
            right: "8px",
            padding: "4px 8px",
            background: color.surface,
            borderRadius: "4px",
            fontSize: "11px",
            color: color.textSecondary,
            fontWeight: "600",
          }}
        >
          {page.cutCount} cuts
        </div>
      </div>

      {/* 페이지 정보 */}
      <div
        style={{
          padding: "12px",
          borderTop: `1px solid ${color.border}`,
        }}
      >
        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            marginBottom: "6px",
          }}
        >
          <span
            style={{
              color: color.text,
              fontSize: "14px",
              fontWeight: "600",
            }}
          >
            Page {page.pageNumber}
          </span>
          {page.hasComments && <MessageSquare size={14} color={color.accent} />}
        </div>

        {page.memo && (
          <p
            style={{
              color: color.textSecondary,
              fontSize: "12px",
              lineHeight: "1.4",
              overflow: "hidden",
              textOverflow: "ellipsis",
              whiteSpace: "nowrap",
            }}
          >
            {page.memo}
          </p>
        )}
      </div>
    </div>
  );
};

