// 전역 데이터 상태 (차후 서버 연동 대비)
import { createContext, useContext, useState } from "react";
import { initialData } from "../services/storage/localStorage";

// context 생성
export const DataContext = createContext();

// provider 컴포넌트
export const DataProvider = ({ children, initialData = { initialData } }) => {
  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  // ● projectState: 서버와 동기화될 데이터
  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  const [data, setData] = useState(initialData);
  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  // ● uiState: 로컬 UI 상태 (서버 저장 X)
  // ━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
  const [uiState, setUiState] = useState({
    currentEpisodeId: initialData.project.episodeOrder[0], // 선택된 에피소드
    currentPageId: null, // 선택된 페이지
    // ... 기타 UI 상태
  });
  // context 훅
  const value = {
    // ● 원본 데이터(읽기/수정)
    // 원본 데이터
    data,
    // 원본 데이터 수정 함수
    setData,

    // ● 헬퍼 값들(자주 쓰는 데이터)
    //-- 현재 에피소드 객체???
    currentEpisode: data.project.episodes[data.project.episodeOrder[0]],
    //-- 현재 에피소드 아이디
    currentEpisodeId: data.project.episodeOrder[0],

    // ● 편의 함수
    //--
    getProject: () => {},
    //--
    getEpisode: (episodeId) => data.project.episodes[episodeId],
    //--
    getPage: (episode, pageId) =>
      data.project.episodes[episodeId].pages[pageId],
  };
  return <DataContext.Provider value={value}>{children}</DataContext.Provider>;
};

// useData 커스텀 훅
export default function useData() {
  const context = useContext(DataContext);

  // Context 밖에서 사용하면 에러
  if (!context) {
    throw new Error("useData는 DataProvider 안에서만 사용 가능합니다");
  }

  return context;
}
